// Input: msg.payload = "10,81,00,01,05,FF,01,0E,F0,01,62,01,D6,00"
let hexList = msg.payload.split(',');
let byteArray = hexList.map(h => parseInt(h.trim(), 16));
msg.payload = Buffer.from(byteArray);
return msg;

// Function to generate a simple, incrementing Transaction ID
// In a real system, you might want something more robust for uniqueness
let currentTid = 0;

function generateTid() {
    currentTid = (currentTid + 1) % 0x10000; // TIDs are 2 bytes (0x0000 to 0xFFFF)
    return currentTid;
}

// Get the raw payload
// const payload = msg.payload; // Not needed here as we are creating the payload

const controllerSEOJ = Buffer.from('05FF01', 'hex');
const batteryDEOJ = Buffer.from('027D1F', 'hex'); // OMRON Battery Object ID
const service = 0x62; // GET service (0x62)

const properties = [
    { epc: 0x80, pdc: 0x00 }, // Operation status
    { epc: 0xE4, pdc: 0x00 }, // Measured Remaining Capacity 1 (%)
    { epc: 0x88, pdc: 0x00 }, // Fault Status
    { epc: 0xC1, pdc: 0x00 }, // Capacity for grid connection
    { epc: 0xC2, pdc: 0x00 }, // Rated power for grid connection
    { epc: 0xDA, pdc: 0x00 }, // Remaining stored electricity amount 2
    { epc: 0x42, pdc: 0x00 }, // Instantaneous Electric Power (test this!)
    { epc: 0x89, pdc: 0x00 }  // Additional discharge-related EPC (optional) //,
    // { epc: 0xCF, pdc: 0x00 } // Vendor-specific battery mode/status
];


let opc = properties.length;
let tid = generateTid(); // Generate a new TID for this request

let bufferParts = [
    Buffer.from('1081', 'hex'),         // EHD (ECHONET Lite Header)
    Buffer.from(tid.toString(16).padStart(4, '0'), 'hex'), // TID (Transaction ID)
    controllerSEOJ,                     // SEOJ (Source ECHONET Object)
    batteryDEOJ,                        // DEOJ (Destination ECHONET Object)
    Buffer.from([service]),             // ESV (ECHONET Service)
    Buffer.from([opc])                  // OPC (Number of properties)
];

properties.forEach(prop => {
    bufferParts.push(Buffer.from([prop.epc, prop.pdc])); // EPC and PDC for each property
});

msg.payload = Buffer.concat(bufferParts);
msg.ip = "192.168.86.181"; // Target IP for Battery
msg.port = 3610;            // ECHONET Lite port
msg.topic = 'omron_battery_data_request_new_epcs'; // Informative topic

// Store the TID in flow context or global context associated with the IP
// This assumes a 'flow' context named 'outstandingRequests'
// You might want to use global context if multiple flows need to access it.
const outstandingRequests = flow.get('outstandingRequests') || {};
if (!outstandingRequests[msg.ip]) {
    outstandingRequests[msg.ip] = new Set(); // Use a Set for efficient TID checking
}
outstandingRequests[msg.ip].add(tid); // Store the TID (decimal for easier comparison)
flow.set('outstandingRequests', outstandingRequests);

return msg;

// Function to convert a hex string (e.g., "0x01") to a decimal number.
function hexToDec(hex) {
    if (typeof hex !== 'string' || !hex.startsWith('0x')) {
        return null; // Or throw an error, depending on desired error handling
    }
    return parseInt(hex.substring(2), 16);
}

// Function to parse the instance list (0xD6)
function parseInstanceList(edt) {
    if (!edt || typeof edt !== 'string' || !edt.startsWith('0x')) {
        return [];
    }
    const hexString = edt.substring(2); // Remove "0x"
    const eojs = [];
    // Each EOJ is 3 bytes (6 hex characters)
    for (let i = 0; i < hexString.length; i += 6) {
        if (i + 6 <= hexString.length) {
            eojs.push('0x' + hexString.substring(i, i + 6).toUpperCase());
        }
    }
    return eojs;
}

// Get the raw payload
const payload = msg.payload;

const TARGET_IP = "192.168.86.181"; // The IP address of your battery (or desired device)
const sourceAddress = msg.ip || msg.fromip || msg.address || "unknown_address"; // Get source IP from msg

// --- START: IP and Request Filtering Logic ---
if (sourceAddress !== TARGET_IP) {
    // Optionally log that you're ignoring a message
    node.warn(`Ignoring ECHONET Lite message from unknown source: ${sourceAddress}`);
    return null; // Stop the flow for this message if it's not from the target IP
}

// Check if payload is a Buffer (raw ECHONET Lite response)
if (!(payload instanceof Buffer)) {
    node.warn("Payload is not a Buffer. Passing through or ignoring.");
    return null; // Or return msg if you want to pass non-Buffer messages
}

// Basic ECHONET Lite header parsing (needed to get TID)
const ehd = payload.readUInt16BE(0); // EHD (ECHONET Lite Header)
const tid = payload.readUInt16BE(2); // TID (Transaction ID)
const seoj = payload.readUIntBE(4, 3); // SEOJ (Source ECHONET Object)
const deoj = payload.readUIntBE(7, 3); // DEOJ (Destination ECHONET Object)
const esv = payload.readUInt8(10); // ESV (ECHONET Service)
const opc = payload.readUInt8(11); // OPC (Number of properties)

// Retrieve outstanding requests from context
const outstandingRequests = flow.get('outstandingRequests') || {};
const deviceRequests = outstandingRequests[sourceAddress];

if (!deviceRequests || !deviceRequests.has(tid)) {
    node.warn(`Ignoring ECHONET Lite response from ${sourceAddress} with TID 0x${tid.toString(16).toUpperCase()}: Not an expected response.`);
    return null; // Ignore if TID not found for this IP
}

// If we reach here, it means the IP matches AND the TID matches an outstanding request.
// Remove the TID from the outstanding requests as it has been received.
deviceRequests.delete(tid);
flow.set('outstandingRequests', outstandingRequests); // Save the updated context
// --- END: IP and Request Filtering Logic ---

let parsedProperties = [];
let offset = 12; // Start of properties data

let statusMessage = "Unknown"; // Default status message

switch (esv) {
    case 0x52: statusMessage = "Get_SNA (Service Not Available)"; break;
    case 0x62: statusMessage = "GET (Request)"; break; // This is a request, not a response
    case 0x72: statusMessage = "Get_Res (Success)"; break; // This is the one we expect for successful GET responses
    case 0x5E: statusMessage = "Inf_SNA (Information Service Not Available)"; break; // For INF responses
    case 0x7E: statusMessage = "Inf_Res (Information Response)"; break; // For INF responses
    case 0x60: statusMessage = "SET_REQ (Set Request)"; break; // Set Request from Device
    case 0x70: statusMessage = "SET_RES (Set Response)"; break; // Set Response from Device
    case 0x50: statusMessage = "SET_SNA (Set Service Not Available)"; break; // Set Service Not Available
    // Add other ESVs as needed
    default: statusMessage = `ESV: 0x${esv.toString(16).toUpperCase()}`; break;
}

// Only parse properties if it's a response (0x72 Get_Res, 0x52 Get_SNA, 0x7E Inf_Res, etc.)
if (esv === 0x72 || esv === 0x52 || esv === 0x7E || esv === 0x5E) { // Expanded for other response types
    for (let i = 0; i < opc; i++) {
        const epc = payload.readUInt8(offset); // EPC (Property Code)
        const pdc = payload.readUInt8(offset + 1); // PDC (Property Data Count)

        let edtBuffer = null;
        let edtHex = null;

        if (pdc > 0) {
            edtBuffer = payload.subarray(offset + 2, offset + 2 + pdc);
            edtHex = '0x' + edtBuffer.toString('hex').toUpperCase();
        } else {
            edtHex = '0x'; // No data if PDC is 0
        }

        let parsedProperty = {
            epc: '0x' + epc.toString(16).toUpperCase(),
            pdc: pdc,
            edt: edtHex,
            status: (esv === 0x72 || esv === 0x7E) ? "Success" : "Not Available" // Status for this specific property
        };

        // Interpret common ECHONET Lite properties
        if (epc === 0x80) { // Operation status
            parsedProperty.propertyName = "Operation Status";
            if (pdc === 1 && edtHex === '0x30') {
                parsedProperty.value = "ON";
            } else if (pdc === 1 && edtHex === '0x31') {
                parsedProperty.value = "OFF";
            } else {
                parsedProperty.value = `Unknown (0x${edtHex.substring(2)})`;
            }
        } else if (epc === 0x81) { // Installation Location
            parsedProperty.propertyName = "Installation Location";
            if (pdc === 1) {
                const locationCode = hexToDec(edtHex);
                // Standard ECHONET Lite location codes (can be extended)
                const locations = {
                    0x01: "Living Room", 0x02: "Dining Room", 0x03: "Kitchen",
                    0x04: "Bathroom", 0x05: "Toilet", 0x06: "Washroom",
                    0x07: "Corridor", 0x08: "Room", 0x09: "Stairway",
                    0x0A: "Front Door", 0x0B: "Balcony", 0x0C: "Garden",
                    0x0D: "Garage", 0x0E: "Carport", 0x0F: "Other"
                };
                parsedProperty.value = locations[locationCode] || `Unknown Code (0x${edtHex.substring(2)})`;
            } else {
                parsedProperty.value = edtHex;
            }
        } else if (epc === 0x88) { // Fault Status
            parsedProperty.propertyName = "Fault Status";
            if (pdc === 1) {
                parsedProperty.value = (edtHex === '0x41') ? "Fault Occurred" : "No Fault";
            } else if (pdc > 1) {
                parsedProperty.value = edtHex; // Keep as hex string for now, for detailed fault codes
            }
        } else if (epc === 0x8A) { // Manufacturer Code
            parsedProperty.propertyName = "Manufacturer Code";
            if (pdc === 3) {
                parsedProperty.value = edtHex; // Example: OMRON's code might be 0x000084 (just an example)
            }
        } else if (epc === 0x9D) { // Supported GET property map (GET property map for Get)
            parsedProperty.propertyName = "Supported GET Property Map (Get)";
            if (pdc > 0) {
                const numProps = hexToDec('0x' + edtHex.substring(2, 4)); // First byte is count
                const supportedEpcs = [];
                for (let j = 0; j < numProps; j++) {
                    const epcByte = edtHex.substring(4 + j * 2, 6 + j * 2);
                    if (epcByte) {
                        supportedEpcs.push('0x' + epcByte.toUpperCase());
                    }
                }
                parsedProperty.value = supportedEpcs;
            } else {
                parsedProperty.value = "No properties listed";
            }
        } else if (epc === 0x9E) { // Supported SET property map (GET property map for Set)
            parsedProperty.propertyName = "Supported SET Property Map (Set)";
            if (pdc > 0) {
                const numProps = hexToDec('0x' + edtHex.substring(2, 4)); // First byte is count
                const supportedEpcs = [];
                for (let j = 0; j < numProps; j++) {
                    const epcByte = edtHex.substring(4 + j * 2, 6 + j * 2);
                    if (epcByte) {
                        supportedEpcs.push('0x' + epcByte.toUpperCase());
                    }
                }
                parsedProperty.value = supportedEpcs;
            } else {
                parsedProperty.value = "No properties listed";
            }
        }
        // Add other device class-specific properties (Storage Battery: 0x027D)
        else if (epc === 0xE4) { // Measured Remaining Capacity 1 (%)
            parsedProperty.propertyName = "Battery Remaining Capacity";
            if (pdc === 1) {
                parsedProperty.value = hexToDec(edtHex);
                parsedProperty.units = "%";
            } else {
                parsedProperty.value = edtHex;
            }
        }
        else if (epc === 0xAA) { // Self-node Instance List S
            parsedProperty.propertyName = "Self-node Instance List S";
            parsedProperty.value = parseInstanceList(edtHex); // Re-using parseInstanceList
        }
        else if (epc === 0xAB) { // Self-node Class List S
            parsedProperty.propertyName = "Self-node Class List S";
            // This property usually lists the device classes supported by the controller itself.
            // Format is similar to instance list but for class codes (2 bytes each)
            if (edtHex && pdc > 0) {
                const hexString = edtHex.substring(2);
                const classes = [];
                for (let j = 0; j < hexString.length; j += 4) { // 2 bytes per class
                    if (j + 4 <= hexString.length) {
                        classes.push('0x' + hexString.substring(j, j + 4).toUpperCase());
                    }
                    }
                parsedProperty.value = classes;
            } else {
                parsedProperty.value = edtHex;
            }
        }
        else if (epc === 0xC1) { // Capacity for grid connection
            parsedProperty.propertyName = "Grid Connection Capacity/Status";
            if (pdc === 1) {
                const val = hexToDec(edtHex);
                // Based on observation, 0x03 seems to be an enabled/ready state
                switch (val) {
                    case 0x00: parsedProperty.value = "Not Connected"; break;
                    case 0x01: parsedProperty.value = "Connected"; break;
                    case 0x02: parsedProperty.value = "Connection Possible"; break;
                    case 0x03: parsedProperty.value = "Connection Enabled/Ready"; break; // Common interpretation for your value
                    default: parsedProperty.value = `Unknown Code (0x${val.toString(16).toUpperCase()})`;
                }
            } else {
                parsedProperty.value = edtHex;
            }
        }
        else if (epc === 0xC2) { // Rated power for grid connection
            parsedProperty.propertyName = "Grid Connection Rated Power Status";
            if (pdc === 1) {
                const val = hexToDec(edtHex);
                // Similar to 0xC1, likely a status/enablement, not a direct power value given 1 byte
                switch (val) {
                    case 0x00: parsedProperty.value = "Disabled"; break;
                    case 0x01: parsedProperty.value = "Enabled"; break;
                    case 0x03: parsedProperty.value = "Enabled (Full Capacity)"; break; // Based on observed 0x03
                    default: parsedProperty.value = `Unknown Code (0x${val.toString(16).toUpperCase()})`;
                }
            } else if (pdc === 2) { // If it were a power value (e.g., Watts)
                const val = parseInt(edtHex.substring(2), 16);
                if (val & 0x8000) { parsedProperty.value = val - 0x10000; } // Signed 16-bit
                else { parsedProperty.value = val; }
                parsedProperty.units = "W";
            } else {
                parsedProperty.value = edtHex;
            }
        }
        else if (epc === 0xCF) { // PV-linked status
            parsedProperty.propertyName = "PV-Linked Status";
            if (pdc === 1) {
                const val = hexToDec(edtHex);
                switch (val) {
                    case 0x00: parsedProperty.value = "Not Linked"; break;
                    case 0x01: parsedProperty.value = "Linked"; break;
                    case 0x02: parsedProperty.value = "Operating PV"; break; // Example interpretation
                    default: parsedProperty.value = `Unknown Code (0x${val.toString(16).toUpperCase()})`;
                }
            } else {
                parsedProperty.value = edtHex;
            }
        }
        else if (epc === 0xDA) { // Remaining stored electricity amount 2 (likely detailed status)
            parsedProperty.propertyName = "Battery Detailed Status";
            if (pdc === 1) {
                const val = hexToDec(edtHex);
                // Based on ECHONET Lite common patterns and your observed 0x46 for 100%
                switch (val) {
                    case 0x41: parsedProperty.value = "Charging"; break;
                    case 0x42: parsedProperty.value = "Discharging"; break;
                    case 0x43: parsedProperty.value = "Idle / Standby"; break;
                    case 0x44: parsedProperty.value = "Full / Charged"; break;
                    case 0x45: parsedProperty.value = "Error / Malfunction"; break;
                    case 0x46: parsedProperty.value = "Charging Complete / Full Standby"; break; // Based on your observation (100% capacity)
                    // You will need to confirm these mappings by observing the battery!
                    default: parsedProperty.value = `Unknown Code (0x${val.toString(16).toUpperCase()})`;
                }
            } else {
                parsedProperty.value = edtHex;
            }
        }
        // General "Unknown EPC" fallback
        else {
            parsedProperty.propertyName = `Unknown EPC 0x${epc.toString(16).toUpperCase()}`;
        }

        parsedProperties.push(parsedProperty);
        offset += (2 + pdc); // Move offset to the next property
    }
} else {
    node.warn(`Received ECHONET Lite message with unexpected ESV: 0x${esv.toString(16).toUpperCase()} for DEOJ: 0x${deoj.toString(16).toUpperCase()}`);
    node.warn(`Raw payload: ${payload.toString('hex')}`);
    return null; // Don't process non-response ESVs if you only want responses
}

// Set msg.payload to the parsed object
msg.payload = {
    timestamp: Date.now(),
    remoteAddress: sourceAddress, // This will now always be the TARGET_IP if it passes the filter
    ehd: '0x' + ehd.toString(16).toUpperCase(),
    tid: '0x' + tid.toString(16).toUpperCase(),
    seoj: '0x' + seoj.toString(16).toUpperCase(),
    deoj: '0x' + deoj.toString(16).toUpperCase(),
    esv: '0x' + esv.toString(16).toUpperCase(),
    opc: opc,
    status: statusMessage,
    properties: parsedProperties
};

flow.set("batteryData", msg.payload); 
msg.topic = "battery";
return msg;

const controllerSEOJ = Buffer.from('05FF01', 'hex');
const nodeProfileDEOJ = Buffer.from('0EF001', 'hex'); // Target Node Profile
const service = 0x62; // GET service
const epc = 0xD6; // Self-node instance list S
const pdc = 0x00; // For GET

const tid = Buffer.from('0001', 'hex'); // Transaction ID

const buffer = Buffer.concat([
    Buffer.from('1081', 'hex'),
    tid,
    controllerSEOJ,
    nodeProfileDEOJ,
    Buffer.from([service]),
    Buffer.from([0x01]), // OPC (1 property)
    Buffer.from([epc, pdc])
]);

msg.payload = buffer;
msg.ip = "224.0.23.0"; // Multicast address for discovery
msg.port = 3610;
msg.topic = 'discovery_request';
return msg;

// Generate unique TID
let tid = Math.floor(Math.random() * 0xffff);
flow.set("expected_tid", tid);

let tidBuffer = Buffer.alloc(2);
tidBuffer.writeUInt16BE(tid);

// Compose ECHONET Lite frame
msg.payload = Buffer.concat([
  Buffer.from([0x10, 0x81]),      // EHD
  tidBuffer,                      // TID
  Buffer.from([0x02, 0x88]),      // SEOJ
  Buffer.from([0x05, 0xFF]),      // DEOJ
  Buffer.from([0x62, 0x01, 0xE0, 0x00]) // ESV, OPC, EPC, PDC
]);

msg.port = 3610;
msg.ip = "192.168.x.x"; // Replace with battery IP
return msg;

if (flow.get("expected_tid") !== null) {
    flow.set("expected_tid", null);
    msg.payload = { error: "Battery device did not respond in time." };
    msg.statusCode = 504;
    return msg;
}
return null;

let payload = msg.payload;
let receivedTid = payload.readUInt16BE(2);
let expectedTid = flow.get("expected_tid");
if (receivedTid !== expectedTid) return null; // Ignore unrelated response
flow.set("expected_tid", null);

// Parse EPC E0 (Battery Capacity)
let epcIndex = payload.indexOf(0xE0);
if (epcIndex !== -1 && payload[epcIndex + 1] > 0) {
    let pdc = payload[epcIndex + 1];
    let dataBytes = payload.slice(epcIndex + 2, epcIndex + 2 + pdc);
    let capacity = dataBytes.readUInt16BE(); // adjust based on actual format
    msg.payload = { batteryCapacity: capacity + " Wh" };
} else {
    msg.payload = { error: "Malformed battery data" };
    msg.statusCode = 500;
}
return msg;

msg.payload = { error: msg.error.message || "Unhandled error" };
msg.statusCode = 500;
return msg;

// Function node to request 0x9D (Supported GET Property Map) from a hypothetical Solar Power Generation Device object on Cube J1
const controllerSEOJ = Buffer.from('05FF01', 'hex'); // Your controller's SEOJ
const solarPanelDEOJ = Buffer.from('028801', 'hex'); // Solar Power Generation Device, instance 01 (common)
const service = 0x62; // GET service

const epc1 = 0x9D; // Requesting the Supported GET Property Map
const pdc1 = 0x00; // PDC is 0x00 for a GET request

const tid = Buffer.from('000A', 'hex'); // Increment TID

const buffer = Buffer.concat([
    Buffer.from('1081', 'hex'), // EHD
    tid, // TID
    controllerSEOJ,              // SEOJ
    solarPanelDEOJ,              // DEOJ (Targeting the solar panel object)
    Buffer.from([service]),      // ESV
    Buffer.from([0x01]),         // OPC (1 property)
    Buffer.from([epc1, pdc1])
]);

msg.payload = buffer;
msg.ip = "192.168.86.225"; // Cube J1's IP
msg.port = 3610;
msg.topic = 'cube_j1_solar_028801_9D_request';
return msg;

const htmlContent = msg.payload; // msg.payload will contain the script/HTML from top_val.cgi
const data = {};

// Helper function to extract a variable value from the script content
// It specifically looks for 'var varNameT = "VALUE";' or 'var varNameT = VALUE;' format
function extractScriptVar(varName) {
    const regex = new RegExp(`var\\s+${varName}T\\s*=\\s*["']?([^"';]*)["']?;`);
    const match = htmlContent.match(regex);

    if (match && match[1] !== undefined) {
        let value = match[1].trim();

        // Handle the specific case of '&nbsp;' for empty fields
        value = value.replace(/&nbsp;/g, '').trim();

        // Convert common "empty" indicators to 0 for numerical fields
        if (value === "" || value === "-") {
            return 0; // Return 0 for empty or "-" for numerical interpretation
        }

        // Attempt to parse as a float (for numbers)
        const parsedValue = parseFloat(value);
        if (!isNaN(parsedValue)) {
            return parsedValue; // Return as a number if successful
        }
        // If it's not a number (e.g., "bunkion"), return the cleaned string value
        return value;
    }
    return null; // Return null if the variable is not found in the content
}

// Extracting all the variables identified in your top_val.cgi response
// These names correspond to the 'varNameT' in the AiSEG's top_val.cgi script
data.selElec = extractScriptVar('selElec'); // Electricity Sold (å£²é›»)
data.selUnit = extractScriptVar('selUnit'); // Selling Unit Price (å£²é›»å˜ä¾¡)
data.buyElec = extractScriptVar('buyElec'); // Electricity Bought (è²·é›»)
