data.enefarm = extractScriptVar('enefarm'); // Ene-Farm/Fuel Cell Generation (ã‚¨ãƒãƒ•ã‚¡ãƒ¼ãƒ )
data.soler = extractScriptVar('soler');     // Total Solar Power Generation (from solerT)
