data.batteryCharge = extractScriptVar('batteryCharge'); // Battery Charging Status (-1: off, 0: not charging, 1: charging)
data.batteryPer = extractScriptVar('batteryPer'); // Battery Percentage (è“„é›»æ± æ®‹é‡ãƒ‘ãƒ¼ã‚»ãƒ³ãƒˆ)
data.use = extractScriptVar('use');         // Current Usage/Consumption (ä½¿ç”¨é‡)
