}

msg.payload = data; // Set the payload to an object containing all extracted data
msg.topic = "solar"; 
flow.set("solarData", msg.payload); 
return msg;

// Extract Open-Meteo values
let current = msg.payload.current || {};
let temp = current.temperature_2m;
let code = current.weathercode;

let emoji = "â”";
let iconName = "weather-alert";

switch (code) {
    case 0: emoji = "â˜€ï¸"; iconName = "weather-clear"; break;            // Clear sky
    case 1: emoji = "ðŸŒ¤ï¸"; iconName = "weather-mostly-clear"; break;     // Mainly clear
    case 2: emoji = "â›…"; iconName = "weather-partly-cloudy"; break;     // Partly cloudy
    case 3: emoji = "â˜ï¸"; iconName = "weather-cloudy"; break;            // Overcast
    case 45: case 48: emoji = "ðŸŒ«ï¸"; iconName = "weather-hazy"; break;   // Fog & depositing rime
