let baseline = 0.35; // average "always-on" load/use, based on observation and Japanese household average
// low rate energy period: 23:00 - 07:00

// settings from battery system
const batterySafetyCharge = 30; // charge kept at all times as an emergency backup
const batteryMaximumLowRateCharge= 50; // maximum to which battery charges from the net after 23:00 (if needed) with low rate nighttime energy
// note that AI in the battery system can override the values and keep a 100% charge at all times in case of bad weather or emergencies.

// --- Retrieve battery & solar ---
let solar = flow.get("solarData");
let battery = flow.get("batteryData");
if (!solar || !battery || !battery.properties) return [null];

// --- Extract values ---
let gen = solar.soler || 0;
let use = solar.use || 0;
let buying = solar.buyElec || 0;
let selling = solar.selElec || 0;
let battery_pct = battery.properties.find(p => p.epc === "0xE4")?.value;
if (battery_pct === undefined) return [null];

// --- Configurable Parameters ---
const capacity = 8.8; // Usable battery capacity in kWh (nominal 9.8 kWh)
const intervalMinutes = 5;
const durationMinutes = flow.get("durationMinutes") || 30;
const windowSize = durationMinutes / intervalMinutes;

const nominalSolarCapacity = 5.04; // in kW, based on installer spec
if (gen > nominalSolarCapacity) {
    gen = nominalSolarCapacity; // clamp to expected max
}

let solarEfficiency = 0;
if (nominalSolarCapacity > 0) {
    solarEfficiency = gen / nominalSolarCapacity;
}

// --- History Tracking ---
let pctHistory = flow.get("batteryPctHistory") || [];
pctHistory.push(battery_pct);
if (pctHistory.length > windowSize) pctHistory.shift();
flow.set("batteryPctHistory", pctHistory);

// --- Delta Calculation ---
let delta = 0;
if (pctHistory.length === windowSize) {
    delta = battery_pct - pctHistory[0];
}
const maxBatteryRate = 4.0; // max charge/discharge throughput (kW)

// --- Rate Calculation ---
let delta_pct = pctHistory.length === windowSize ? battery_pct - pctHistory[0] : 0;
let rate_kWh = (delta_pct / 100) * capacity;
let rate_kW = rate_kWh / (durationMinutes / 60); // Convert duration to hours
rate_kW = Math.max(Math.min(rate_kW, maxBatteryRate), -maxBatteryRate);

// --- Current Rate (short-term) ---
let currentRate_kW = 0;
if (pctHistory.length >= 2) {
    let shortDelta_pct = battery_pct - pctHistory[pctHistory.length - 2];
    let shortRate_kWh = (shortDelta_pct / 100) * capacity;
    currentRate_kW = shortRate_kWh / (intervalMinutes / 60); // Convert interval to hours
    currentRate_kW = Math.max(Math.min(currentRate_kW, maxBatteryRate), -maxBatteryRate);
}

// --- Moving Average for Graph Smoothing ---
let rateBuffer = flow.get("batteryRateHistory") || [];
rateBuffer.push(currentRate_kW);
if (rateBuffer.length > windowSize) rateBuffer.shift();
flow.set("batteryRateHistory", rateBuffer);

let smoothed_rate_kW = rateBuffer.reduce((a, b) => a + b, 0) / rateBuffer.length;
smoothed_rate_kW = Math.max(Math.min(smoothed_rate_kW, maxBatteryRate), -maxBatteryRate);

// --- Confidence Tagging ---
let confidence = "high";
if (Math.abs(delta_pct) < 0.3 && Math.abs(rate_kW) === 0 && gen > 0) {
    confidence = "low"; // possible telemetry rounding
}

// --- Determine Idle Status More Accurately ---
let isIdle = rate_kW === 0 && battery_pct === pctHistory[pctHistory.length - 1] && gen < use && buying === 0;

// --- Status Logic ---
let status = "idle";
if (battery_pct > 30) {
    if (buying === 0 && gen < use) status = "discharging";
    if (buying > 0 && gen < use) status = "idle";
    if (gen >= use && battery_pct > pctHistory[0]) status = "charging";
}
if (currentRate_kW < 0) status = "discharging";
if (currentRate_kW === 0) status = "idle";
if (currentRate_kW > 0) status = "charging";

const minSafePct = 10;
if (battery_pct < minSafePct) status = "idle"; // prevent misleading discharge classification


// --- Household Calculation ---
let adjustedUse = use; // default: use equals household consumption

// If household is silently powered by battery (use suppressed during discharge)
if (status === "discharging" && use <= baseline && rate_kW < 0) {
    adjustedUse = Math.abs(rate_kW); // Use is masked, but battery discharge reveals real consumption
}

// If charging from grid, remove battery charge contribution from household
if (status === "charging" && buying > 0) {
    let batteryGridCharge = Math.min(rate_kW, buying);
    adjustedUse = Math.max(0, use - batteryGridCharge); // Avoid negative use
}

let household = parseFloat(adjustedUse.toFixed(2));

let household_source = "direct-metered";
if (status === "discharging" && use <= baseline && rate_kW < 0) {
    household_source = "inferred-from-battery";
}

let chargingSource = "unknown";
if (status === "charging") {
    if (gen > use) chargingSource = "solar";
    else if (buying > 0) chargingSource = "grid";
}
if (status === "charging") {
    if (gen > use + rate_kW) chargingSource = "solar-surplus";
    else if (buying >= rate_kW) chargingSource = "grid";
}

// --- Weather ---
let weather = flow.get("weatherData");
let sunData = flow.get("sunData");

// --- Timestamp ---
let timestamp = new Date().toLocaleTimeString("ja-JP", {
    hour: "2-digit",
    minute: "2-digit",
    hour12: false,
    timeZone: "Asia/Tokyo"
});

// --- Final Output ---
msg.payload = {
    timestamp,
    sundata: {
        sunrise: sunData.sunrise, 
        sunset: sunData.sunset
    },    
    solar: {
        generation_kW: gen,
        usage_kW: use,
        buy_kW: buying,
        sell_kW: selling,
        efficiency: parseFloat(solarEfficiency.toFixed(3))
    },
    battery: {
        percent: battery_pct,
        status: status,
        rate_kW: parseFloat(smoothed_rate_kW.toFixed(2)),     // For graph
        current_rate_kW: parseFloat(currentRate_kW.toFixed(2)), // For real-time logic
        source: chargingSource,
        confidence,
        isIdle
    },
    calculation: {
        household_kW: parseFloat(household.toFixed(2)),
        delta_pct: pctHistory.length === windowSize ? delta : null,
        durationMinutes,
        capacity_kWh: capacity
    },
    weather
};

global.set("energyData", msg.payload);

return msg;

// Extract and store sunrise/sunset data
