// Controller & target (Node Profile Object)
const controllerSEOJ = Buffer.from("05FF01", "hex");
const nodeProfileDEOJ = Buffer.from("0EF001", "hex");
const serviceCode = 0x62; // ESV: Get

// EPCs we want to query
const epcs = ["80", "8A", "9F", "9E"];
const opc = epcs.length;

// Build frame header
const ehd = Buffer.from("1081", "hex"); // ECHONET Lite header
const tid = Buffer.from("1234", "hex");  // Transaction ID
const seoj = controllerSEOJ;
const deoj = nodeProfileDEOJ;
const esv = Buffer.from([serviceCode]);
const opcBuf = Buffer.from([opc]);

const tidLabelMap = flow.get('tidLabelMap') || {};
tidLabelMap[tid] = 'node_profile';
flow.set('tidLabelMap', tidLabelMap);

// Build property requests (EPC + PDC=0)
const epcBlocks = Buffer.concat(epcs.map(code => Buffer.from([parseInt(code, 16), 0x00])));

const frame = Buffer.concat([ehd, tid, seoj, deoj, esv, opcBuf, epcBlocks]);

msg.payload = frame;
msg.ip = "224.0.23.0"; // ECHONET Lite multicast address
msg.port = 3610;
msg.tid = "1234";
msg.topic = "ECHONET_NodeProfile_Query";
return msg;

const payload = msg.payload;
const sourceAddress = msg.ip || msg.fromip || msg.address || "unknown_address";
const TARGET_IP = "192.168.86.181";
const logAllEPCs = true;

if (sourceAddress !== TARGET_IP || !(payload instanceof Buffer)) {
    return null;
}

const ehd = payload.readUInt16BE(0);
const tid = payload.readUInt16BE(2);
const esv = payload.readUInt8(10);
const opc = payload.readUInt8(11);

let offset = 12;
const parsed = [];

for (let i = 0; i < opc; i++) {
    const epc = payload.readUInt8(offset);
    const pdc = payload.readUInt8(offset + 1);
    const edt = payload.subarray(offset + 2, offset + 2 + pdc);
    const edtHex = edt.length ? '0x' + edt.toString('hex').toUpperCase() : '0x';
    const status = (esv === 0x72 || esv === 0x7E) ? "Success" : "Not Available";
    if (!logAllEPCs && status !== "Success") {
        offset += (2 + pdc);
        continue;
    }

    let propertyName = `EPC 0x${epc.toString(16).toUpperCase()}`;
    let value = edtHex;
    let units = "";

    switch (epc) {
        case 0xE4:
            propertyName = "Battery Remaining Capacity";
            value = edt.readUInt8(0); // e.g. 93
            units = "%";
            break;
        case 0xDA:
            propertyName = "Battery Detailed Status";
            value = {
                0x41: "Charging",
                0x42: "Discharging",
                0x46: "Charging Complete / Full Standby"
            }[edt[0]] || `Code ${edtHex}`;
            break;
        case 0xC1:
            propertyName = "Grid Connection Status";
            value = {
                0x00: "Not Connected",
                0x03: "Enabled / Ready"
            }[edt[0]] || `Code ${edtHex}`;
            break;
        case 0xC2:
            propertyName = "Grid Rated Power Status";
            value = {
                0x01: "Enabled (Partial)",
                0x03: "Enabled (Full Capacity)"
            }[edt[0]] || `Code ${edtHex}`;
            break;
        case 0x80:
            propertyName = "Operation Status";
            value = edt[0] === 0x30 ? "ON" : edt[0] === 0x31 ? "OFF" : `Code ${edtHex}`;
            break;
        case 0x88:
            propertyName = "Fault Status";
            value = edt[0] === 0x42 ? "No Fault" : `Code ${edtHex}`;
            break;
        case 0x42:
            propertyName = "Unknown EPC 0x42";
            value = edtHex;
            break;
        case 0x89:
            propertyName = "Unknown EPC 0x89";
            value = edtHex;
            break;
        default:
            // Leave as default label and raw value
            break;
    }

    parsed.push({
        Timestamp: new Date().toISOString(),
        EPC: '0x' + epc.toString(16).toUpperCase(),
        EDT: edtHex,
        Status: status,
        Source: sourceAddress,
        propertyName,
        value,
        units
    });

    offset += (2 + pdc);
}

return { payload: parsed };

